import { Component } from "react"
import { ContactForm } from "./ContactForm/ContactForm"
// import { nanoid } from 'nanoid'

export class App extends Component {
  state = {
  contacts: [],
  name: ''
}

addNameHandler = e => {
  e.preventDefault()
}
handleNameChange = e => {
  this.setState({name: e.currentTarget.value})
}

render () {
  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 40,
        color: '#010101'
      }}
    >
      <h1>Phonebook</h1>
      < ContactForm handleSubmit={this.addNameHandler}/>
      <h1>Contacts</h1>
     
    </div>
  );
  }
};
