import { Component } from "react"

export class App extends Component {
  state = {
  contacts: [],
  name: ''
}
render () {
  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 40,
        color: '#010101'
      }}
    >
      <h1>Phonebook</h1>
      <label htmlFor="name">Name</label>
      <input type="text" name="name" required />
      <button type="burron">Add contact</button>
    </div>
  );
  }
};
