import { Component } from "react"
// import { nanoid } from 'nanoid'

export class App extends Component {
  state = {
  contacts: [],
  name: ''
}

addNameHandler = e => {
  e.preventDefault()
}
handleNameChange = e => {
  this.setState({name: e.currentTarget.value})
}

render () {
  const {name} = this.state
  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 40,
        color: '#010101'
      }}
    >
      <form onSubmit={this.handleSubmit}>
      <h1>Phonebook</h1>
      <label>Name</label>
      <input type="text" onChange={this.handleNameChange} value={name} name="name" required />
      <button type="submit" onClick={this.addNameHandler}>Add contact</button>
      </form>
      <h1>Contacts</h1>
      {/* <ul className="contactsList">
        <li className="contact">{this.state.name}</li>
      </ul> */}
    </div>
  );
  }
};
